(1524, 2, '2013-11-08 08:12:05', '2013-11-08 08:12:05', '[parallax]\r\n\r\n[parallax_section id=''1'' height='''' content_position=''center'']\r\n\r\n[separator type=''transparent'' color='''' thickness='''' up=''25'' down=''25'']\r\n\r\n[four_col_col1]\r\n\r\n[element_fade_in transition_delay=''0'']\r\n<p style="text-align: center;"><a href="http://www.skygatemedia.com/clients.hold/lifesports/V3/wp-content/uploads/2013/09/icon1.png"><img class="aligncenter size-full wp-image-1506" alt="icon1" src="http://www.skygatemedia.com/clients.hold/lifesports/V3/wp-content/uploads/2013/09/icon1.png" width="100" height="100" /></a></p>\r\n<p style="text-align: center;">[separator type=''transparent'' color='''' thickness='''' up=''20'' down=''20'']</p>\r\n\r\n<h4 style="text-align: center;"><strong><span style="color: #8eb438;">Sport Experience</span></strong></h4>\r\n<p style="text-align: center;">[separator type=''transparent'' color='''' thickness='''' up=''10'' down=''10'']</p>\r\n<p style="text-align: center;">The entire complex will be dominated by the latest in display and digital technology that will allow our guests to interact directly with LifeSports USA</p>\r\n<p style="text-align: center;">[separator type=''transparent'' color='''' thickness='''' up=''5'' down=''5'']</p>\r\n<p style="text-align: left;">[/element_fade_in]</p>\r\n[/four_col_col1]\r\n[four_col_col2]\r\n\r\n[element_fade_in transition_delay=''0.4'']\r\n\r\n<a href="http://www.skygatemedia.com/clients.hold/lifesports/V3/wp-content/uploads/2013/09/icon3.png"><img class="aligncenter size-full wp-image-1504" alt="icon3" src="http://www.skygatemedia.com/clients.hold/lifesports/V3/wp-content/uploads/2013/09/icon3.png" width="100" height="100" /></a>\r\n<p style="text-align: center;">[separator type=''transparent'' color='''' thickness='''' up=''20'' down=''20'']</p>\r\n\r\n<h4 style="text-align: center;"><strong><span style="color: #8eb438;">Guest Experience</span></strong></h4>\r\n<p style="text-align: center;">[separator type=''transparent'' color='''' thickness='''' up=''10'' down=''10'']</p>\r\n<p style="text-align: center;">LifeSports USA will provide its guests with an unparalleled level of customer service and amenities</p>\r\n<p style="text-align: center;">[separator type=''transparent'' color='''' thickness='''' up=''5'' down=''5'']</p>\r\n<p style="text-align: left;">[/element_fade_in]</p>\r\n[/four_col_col2]\r\n[four_col_col3]\r\n\r\n[element_fade_in transition_delay=''0.8'']\r\n<p style="text-align: center;"><a href="http://www.skygatemedia.com/clients.hold/lifesports/V3/wp-content/uploads/2013/09/icon5.png"><img class="aligncenter size-full wp-image-1510" alt="icon5" src="http://www.skygatemedia.com/clients.hold/lifesports/V3/wp-content/uploads/2013/09/icon5.png" width="100" height="100" /></a></p>\r\n<p style="text-align: center;">[separator type=''transparent'' color='''' thickness='''' up=''20'' down=''20'']</p>\r\n\r\n<h4 style="text-align: center;"><strong><span style="color: #8eb438;">Food Experience</span></strong></h4>\r\n<p style="text-align: center;">[separator type=''transparent'' color='''' thickness='''' up=''10'' down=''10'']</p>\r\n<p style="text-align: center;">LifeSportsUSA will provide a wide range of food and beverage options with a focus on health and wellness; nutritional education will be one of our primary goals</p>\r\n<p style="text-align: center;">[separator type=''transparent'' color='''' thickness='''' up=''5'' down=''5'']</p>\r\n<p style="text-align: left;">[/element_fade_in]</p>\r\n[/four_col_col3]\r\n[four_col_col4]\r\n\r\n[element_fade_in transition_delay=''1.2'']\r\n<p style="text-align: center;"><a href="http://www.skygatemedia.com/clients.hold/lifesports/V3/wp-content/uploads/2013/09/icon4.png"><img class="aligncenter size-full wp-image-1503" alt="icon4" src="http://www.skygatemedia.com/clients.hold/lifesports/V3/wp-content/uploads/2013/09/icon4.png" width="100" height="100" /></a></p>\r\n<p style="text-align: center;">[separator type=''transparent'' color='''' thickness='''' up=''20'' down=''20'']</p>\r\n\r\n<h4 style="text-align: center;"><strong><span style="color: #8eb438;">Retail Experience</span></strong></h4>\r\n<p style="text-align: center;">[separator type=''transparent'' color='''' thickness='''' up=''10'' down=''10'']</p>\r\n<p style="text-align: center;">LifeSportsUSA will feature a specialty retail district targeting the healthy and active lifestyle demographic, and focusing exclusively on sports, health and nutrition</p>\r\n<p style="text-align: center;">[separator type=''transparent'' color='''' thickness='''' up=''5'' down=''5'']</p>\r\n<p style="text-align: left;">[/element_fade_in]</p>\r\n[/four_col_col4]\r\n\r\n[separator type=''full_width'' color='''' thickness='''' up=''60'' down=''40'']\r\n<p style="text-align: center;">[custom_font font_family='''' font_size=''56'' line_height=''56'' font_style='''' font_weight=''100'' color=''#339966'' text_decoration='''' background_color='''' padding=''0px'' margin=''0px''] <strong>LifeSports USA Gallery</strong> [/custom_font]</p>\r\n[separator type=''transparent'' color='''' thickness='''' up=''25'' down=''25'']\r\n\r\n[portfolio_list type=''hover_text'' columns=''3'' order_by=''menu_order'' order=''ASC'' number=''6'' category=''Gallery'' selected_projects='''' filter=''yes'' lightbox=''yes'' show_load_more=''yes'']\r\n\r\n[separator type=''transparent'' color='''' thickness='''' up=''35'' down=''35'']\r\n\r\n[/parallax_section]\r\n\r\n[parallax_section id=''2'' height=''600'' content_position=''center'']\r\n\r\n[separator type=''transparent'' color='''' thickness='''' up=''45'' down=''45'']\r\n<p style="text-align: center;">[custom_font font_family='''' font_size=''56'' line_height=''56'' font_style='''' font_weight=''100'' color=''#ffffff'' text_decoration='''' background_color='''' padding=''0px'' margin=''0px'' text_shadow=''0px 1px #000000''] <strong>What Our Clients Say</strong> [/custom_font]</p>\r\n<p style="text-align: left;">[separator type=''transparent'' color='''' thickness='''' up=''25'' down=''25'']</p>\r\n[testimonials type=''transparent'' testimonialImageLink1=''http://www.skygatemedia.com/clients.hold/lifesports/V3/wp-content/uploads/2013/09/testimonial1.png'' testimonialImageLink2=''http://www.skygatemedia.com/clients.hold/lifesports/V3/wp-content/uploads/2013/09/testimonial2.png'' testimonialImageLink3=''http://www.skygatemedia.com/clients.hold/lifesports/V3/wp-content/uploads/2013/09/testimonial3.png'']\r\n\r\n[testimonial id=1 name=''- John Doe'' name_color=''#ffffff'' background_color=''#9bc73b'' border_color=''#ffffff'' text_shadow=''0px 1px #000000'']\r\n\r\n<img class="size-full wp-image-310 alignleft" alt="testimonial_quote" src="http://www.skygatemedia.com/clients.hold/lifesports/V3/wp-content/uploads/2013/09/testimonial_quote.png" width="50" height="38" />\r\n<p style="text-align: left;"><span style="color: #ffffff; font-size: 22px; text-shadow: 0px 1px #000000; font-weight: 400;">LifeSportsUSA is a new hybrid of a Youth Sports Complex that will include corporate showcases and specialty retailers targeting the active and health conscious consumer.</span></p>\r\n[/testimonial]\r\n[testimonial id=2 name=''- John Doe'' name_color=''#ffffff'' background_color=''#9bc73b'' border_color=''#ffffff'' text_shadow=''0px 1px #000000'']\r\n\r\n<span style="color: #ffffff; font-size: 22px; text-shadow: 0px 1px #000000; font-weight: 400;"><img class="alignleft" alt="testimonial_quote" src="http://www.skygatemedia.com/clients.hold/lifesports/V3/wp-content/uploads/2013/09/testimonial_quote.png" width="50" height="38" />LifeSportsUSA will become a regional sports destination, attracting youth and adult athletes with competitive national and league tournaments.</span>\r\n\r\n[/testimonial]\r\n[testimonial id=3 name=''- John Doe'' name_color=''#ffffff'' background_color=''#9bc73b'' border_color=''#ffffff'' text_shadow=''0px 1px #000000'']\r\n\r\n<span style="color: #ffffff;